var data = msg.payload;
var name = msg.topic;
var sensorData = flow.get("sensorData");

//(0°C × 9/5) + 32 = 32°F

function unitT(t){
    if(sensorData.tempC === true){
        return t;
    }
    else{
        if(t !== null){
            var tempF = ((t * 9)/5) + 32;
            return tempF;
        }
        else{return t;}
    }
}
function unitP(p){
    if(sensorData.pressureP == true){
      return p;  
    }
    else{
        if(p !== null){
            let pHG = Math.round(p/33.6585);
            return pHG;
        }
        else{return p;}

    }

}

function find(array, attr, value) {
    for(var i = 0; i < array.length; i += 1) {
        if(array[i][attr] === value) {
            return i;
        }
    }
    return -1;
}

var temp, humidity, pressure = null;

//check what sensors are available and assign values
if(data.temperature !== undefined){
    temp = data.temperature;
}
if(data.pressure !== undefined){
    pressure = data.pressure;
}
if(data.humidity !== undefined){
    humidity = data.humidity;
}

var datapoint = {"temperature" :  unitT(temp),
                 "humidity" : humidity,
                 "pressure": unitP(pressure)

}

//find existing sensor data or add a new sensor data
var index = find(sensorData.data, "sensorName", name);


if(index === -1 ){sensorData.data.push({"sensorName": name, "sensorData" : datapoint });}
else{sensorData.data[index] = {"sensorName": name, "sensorData" : datapoint };}

flow.set("sensorData", sensorData);


return msg;
