//This function node has 3 outputs tile types output to relevant outputs
// 1. button (usually carries extra info associated with %extra variable in Tasker)
// 2. toggle (true/false)
// 3. slider (number)
// connect appropriate outputs to receive values from tiles

function reverse(x){
    if(x === "true"){return false;}
    if(x === "false"){return true;}
}

function boolformat(y){
    if(y === "true"){return true;}
    if(y === "false"){return false;}
}

function define(z){
    if(z === undefined){return " ";}
    else{return z;}
}

var config = JSON.parse(msg.payload.config);  
var sourceID = msg.push.senderId;
var toggleReversed, toggle, rangeCurrent, tileAction 


//common tile fields - all tile share these
var tileType = config.tileType;
var tileID = msg.payload.tileID;
var title = config.title;
var subtitle = config.subtitle;
var icon = config.icon;

//extra fields
//toggle = msg.payload.tileStatus;
var taskerCommand = msg.payload.taskerCommand;


//extra info injected by other Tasker actions in front
var extra = msg.payload.extra;

//range config
var rangeMin =     define(config.range.min);
var rangeMax =     define(config.range.max);
var rangeStep =    define(config.range.step);
var rangeFormat =  define(config.range.format);


//process Toggle changes
if(tileType === "toggle"){
    toggle =  boolformat(msg.payload.tileStatus);}

//process range changes
if(tileType === "range"){
    rangeCurrent =  msg.payload.tileStatus;
    toggle = true;
}
//process range toggle
if(tileType === "rangeToggle"){
    toggle =       msg.payload.tileStatus[0];
    rangeCurrent = msg.payload.tileStatus[1];  //toggling rangeToggle defaults to current Range Value in Tasker
    tileAction =   msg.payload.tileStatus[2];


    //if toggle is pressed
    if (tileAction === "toggle"){
        rangeCurrent = msg.payload.tileStatus[1];
        if(toggle === "false"){
            toggle = boolformat(toggle);
        }
        if(toggle === "true" ){
          toggle = boolformat(toggle);
       }
        if(toggle === "true" && rangeCurrent == rangeMin){
            rangeCurrent = rangeMin + 1;
            toggle = boolformat(toggle);
        }
    }
    //if range  is changed
    if(tileAction === "range"){
        if(rangeCurrent >= rangeMin){toggle = boolformat(toggle);}
    }
}


// save the tile state as ID based flow variable
// command needs "=:=" replaced with ":::"
var command = taskerCommand +":::"+ tileID +":::"+ JSON.stringify(config);

var data = {"extra":extra,
            "sourceID": sourceID,
            "taskerCommand": taskerCommand,
            "tileType": tileType,
            "tileConfig": config,
            "tileID": tileID,
            "toggleState": toggle,
            "rangeState": define(rangeCurrent),
            "command" : command
        };

flow.set(tileID, data);



//split outcomes based on the tile type

 switch(tileType){
    case "button":
        msg.data = {"title": title, "subtitle": subtitle};
        msg.extra = extra;
        msg.tileID = tileID;
        msg.inbound = true;

        return [msg, null, null];


    case "toggle":
        msg.payload = toggle;
        msg.extra = extra;
        msg.data = {"title": title, "subtitle": subtitle};
        msg.tileID = tileID;
        msg.inbound = true;

        return [null, msg, null];


    case "range":
        msg.payload = rangeCurrent;
        msg.extra = extra;
        msg.data = {"title": title, "subtitle": subtitle};
        msg.tileID = tileID;
        msg.inbound = true;

        return [null, null, msg];


    case "rangeToggle":
        if (tileAction === "toggle"){
            msg.payload = toggle;
            msg.label = tileID;
            msg.inbound = true;
            msg.tileID = tileID;

            return [null, msg, null];
        }
        if (tileAction === "range"){
            msg.payload = rangeCurrent;
            msg.label = tileID;
            msg.inbound = true;
            msg.tileID = tileID;

            return [null, null, msg];
        }
 }

