var tile, comm, title, tileID, subtitle, icon, command, sourceID, toggle, config, rangeMin, rangeMax, rangeCurrent, rangeStep, rangeFormat, extra

function isNumeric(value) {
        return /^-?\d+$/.test(value);
    }



//Tasker -> NodeRED -> Tasker
if(msg.inbound === true){
    let data = flow.get("thermo");    //replace the name with flow contect name from Settings
    tile = flow.get(msg.tileID);

    sourceID = data.sourceID;
    var lastTileData = {"tileID" : msg.tileID, "sourceID": sourceID};
    context.set("lastTileID", lastTileData);



    //if you want to change the properties of the tile in the response, edit these, otherwise 
    //they will take values from the source tile

    title =         tile.tileConfig.title;
    tileID =        msg.tileID 
    tileType =      tile.tileConfig.tileType;
    subtitle =      tile.tileConfig.subtitle;
    rangeCurrent =  tile.rangeState;
    toggleState  =  tile.toggleState;

    icon =          tile.tileConfig.icon.off;
    if(toggleState === true){icon = tile.tileConfig.icon.on;}
    if(toggleState === false){icon = tile.tileConfig.icon.off;}
    command =       tile.command;
    rangeMin =      tile.tileConfig.range.min;
    rangeMax =      tile.tileConfig.range.max;
    rangeStep =     tile.tileConfig.range.step;
    rangeFormat =   tile.tileConfig.range.format;
    config =        tile.tileConfig;

}






//local response NodeRED -> Tasker
else{
    var lastTile = flow.get("thermo");   //replace the name with flow contect name from Settings
    tile = flow.get(lastTile.tileID);
    sourceID = lastTile.sourceID;

    //if you want to change the properties of the tile in the response, edit these, otherwise 
    //they will take values from the source tile

    title =         tile.tileConfig.title; 
    tileType =      tile.tileType;
    tileID  =       tile.tileID;
    subtitle =      tile.tileConfig.subtitle;  
    icon =          tile.tileConfig.icon;
    taskerCommand = tile.taskerCommand;
    extra =         tile.extra;
    rangeMin =      tile.tileConfig.range.min;
    rangeMax =      tile.tileConfig.range.max;
    rangeStep =     tile.tileConfig.range.step;
    rangeFormat =   tile.tileConfig.range.format;
    config =        tile.tileConfig;

    //checks for inputs from various dashboard elements

    if(tileType === "button"){
        toggleState =   msg.payload;
        rangeCurrent =  tile.rangeState;

        if(toggleState === true){
            icon = tile.tileConfig.icon.on;
            subtitle = "online";
        }
        if(toggleState === false){
            icon = tile.tileConfig.icon.off;
            subtitle = "offline";
        }
    }

    if(tileType === "toggle"){
        toggleState =   msg.payload;
        rangeCurrent =  tile.rangeState;

            // toggling icons on tile
        if(toggleState=== true){
            icon =          tile.tileConfig.icon.on;
        } 
        if(toggleState=== false){
            icon =          tile.tileConfig.icon.off;
        }
    }



    if(tileType === "range"){
        toggleState =   tile.toggleState;
        rangeCurrent =  msg.payload;
    }



    if(tileType === "rangeToggle"){
        if(msg.payload === true){
            toggleState = msg.payload;
            rangeCurrent =   tile.rangeState;
            icon = tile.tileConfig.icon.on;
        } 
        if(msg.payload === false){
            toggleState = msg.payload;
            rangeCurrent =   tile.rangeState;
            icon = tile.tileConfig.icon.off;
        }
        if(isNumeric(msg.payload)){
            node.warn("triggered nums " + msg.payload);
            rangeCurrent =  msg.payload;
            toggleState =   true;
            icon = tile.tileConfig.icon.on;
        }
    }

    //save changes made via nodeRED and update the context
    command = taskerCommand +":::"+ tileID +":::"+ JSON.stringify(config);

    var data = {"extra": extra,
                "sourceID": sourceID,
                "taskerCommand": taskerCommand,
                "tileType": tileType,
                "tileID": tileID,
                "tileConfig": config,
                "toggleState": toggleState,
                "rangeState": rangeCurrent,
                "command" : command
            };
    flow.set(tileID, data);

}


//send join push

comm = "TileUpdate"+"=:="+ tileType +"=:="+ tileID +"=:="+ title +"=:="+ subtitle +"=:="+ icon +"=:="+ command+"=:="+toggleState +"=:="+ rangeMin+ "=:="+ rangeMax +"=:="+ rangeCurrent +"=:="+rangeStep+"=:="+ rangeFormat;

msg.push = {"devices" : sourceID, "text": comm};
return msg;

